(()=>{var l="Dark mode",o="Light mode",t="light",m="dark",n=document.getElementById("theme-switcher");n.innerHTML=localStorage.theme===t?l:o;n.addEventListener("click",function(){let e=localStorage.theme,c=e===t?m:t,{classList:i}=document.documentElement,r=c===t?l:o;i.remove(e),i.add(c),localStorage.theme=c,n.innerHTML=r});document.querySelectorAll(".md ul").forEach(e=>{/<li><input .+>.+<\/li>/.test(e.innerHTML)&&e.classList.add("ul-checkbox")});})();
