(()=>{var o="Dark mode",r="Light mode",t="light",m="dark",n=document.getElementById("theme-switcher");n.innerHTML=localStorage.theme===t?o:r;n.addEventListener("click",function(){let e=localStorage.theme,c=e===t?m:t,{classList:i}=document.documentElement,l=c===t?o:r;i.remove(e),i.add(c),localStorage.theme=c,n.innerHTML=l});document.querySelectorAll(".md ul").forEach(e=>{/<li><input .+>.+<\/li>/.test(e.innerHTML)&&e.classList.add("ul-checkbox")});})();
